char level2(int r, int c) {
	char obstacles[25][80] = {};

	std::ifstream file("level2.txt");
	string row;
	for (int i=0; i < 25; i++) {
		for (int j=0; j < 80; j++) {
			obstacles[i][j] = 0;
		}
	}
	
	for (int i=0; i < 25; i++) {
		if (file >> row){
			for (int j=0; j != std::min<int>(80,row.length()); ++j) {
				obstacles[i][j] = row[j];
			}
		}else break;
	}

	return obstacles[r][c];
}

